INSERT INTO `stain` (`_id`, `stainof`, `fabric`, `need`, `steps`, `notes`, `img`)
VALUES
	(1,'Hair dye, black or brown','Washable Fabrics','<li>Heavy-duty liquid detergent</li>\n    <li>Dilute solution of all-fabric powdered bleach</li>\n    <li>Liquid chlorine bleach</li>\n    <li>Commercial color remover</li>','<li>Pretreat the  stain with heavy-duty liquid detergent. Rinse. </li>\n    <li>Soak fabric in  dilute solution of all-fabric powdered bleach. </li>\n    <li>If stain  persists and garment is white or colorfast, soak entire garment in diluted  solution of liquid chlorine bleach and water.  Chlorine bleach may change the color of the garment or cause  irreversible damage. Check for bleach  tolerance on a hidden seam. If stain  does not come out in 15 minutes of bleaching, if cannot be removed by  bleaching.</li>\n    <li>If bleaching is  not safe or does not work, use a commercial color remover according to package  directions. Note that color remover will  take out the fabric color as well as the stain.  Do not exceed 160 degrees Fahrenheit water with any synthetic fabrics*.  Launder.</li>','<h3>What you will need</h3>\n					<ul><li>Heavy-duty liquid detergent</li>\n    <li>Dilute solution of all-fabric powdered bleach</li>\n    <li>Liquid chlorine bleach</li>\n    <li>Commercial color remover</li>\n    </ul>\n				\n				\n				\n				<h3>Steps to Clean</h3>\n				<ol>\n    <li>Pretreat the  stain with heavy-duty liquid detergent. Rinse. </li>\n    <li>Soak fabric in  dilute solution of all-fabric powdered bleach. </li>\n    <li>If stain  persists and garment is white or colorfast, soak entire garment in diluted  solution of liquid chlorine bleach and water.  Chlorine bleach may change the color of the garment or cause  irreversible damage. Check for bleach  tolerance on a hidden seam. If stain  does not come out in 15 minutes of bleaching, if cannot be removed by  bleaching.</li>\n    <li>If bleaching is  not safe or does not work, use a commercial color remover according to package  directions. Note that color remover will  take out the fabric color as well as the stain.  Do not exceed 160 degrees Fahrenheit water with any synthetic fabrics*.  Launder.</li>\n  </ol>\n  <p>*<strong>Synthetic Fabrics</strong> are: Acetate, Acrylic, Polyester, Nylon, Olefin, Triacetate  and anything labeled static-resistant, wrinkle-resistant, permanent-press,  no-iron, stain-proof or moth-repellent</p>','68.jpg');
INSERT INTO `stain` (`_id`, `stainof`, `fabric`, `need`, `steps`, `notes`, `img`)
VALUES
	(2,'Hair dye, black or brown','Carpet','<li>Detergent</li><li>White vinegar</li><li>Rubbing alcohol</li><li>Ammonia</li>','<li>Mix one tablespoon of liquid dishwashing detergent       and one tablespoon of <br/>\n    white vinegar with two cups of warm water.</li>\n  <li>Using a clean white cloth, sponge the stain with       the detergent/vinegar solution, blotting frequently with a dry cloth until       the stain disappears.</li>\n  <li>Sponge with cold water. </li>\n  <li>Blot until the liquid is absorbed.</li>\n  <li>Sponge with rubbing alcohol. Blot to remove the       stain.</li>\n  <li>Sponge with cold water.</li>\n  <li>If the stain remains, mix one teaspoon of liquid       dishwashing detergent and one tablespoon of ammonia with two cups of warm       water. Sponge the stain with this solution.  Leave it on the stain for at least 30       minutes, blotting every five minutes with a clean white cloth and more       solution.</li>\n  <li>Sponge  with cold water and blot dry.</li>','<div class=\"boxChem\"><h3>Chemical solvents and supplies</h3><ul><li>Detergent</li><li>White vinegar</li><li>Rubbing alcohol</li><li>Ammonia</li></ul></div><ol>\n  <li>Mix one tablespoon of liquid dishwashing detergent       and one tablespoon of <br/>\n    white vinegar with two cups of warm water.</li>\n  <li>Using a clean white cloth, sponge the stain with       the detergent/vinegar solution, blotting frequently with a dry cloth until       the stain disappears.</li>\n  <li>Sponge with cold water. </li>\n  <li>Blot until the liquid is absorbed.</li>\n  <li>Sponge with rubbing alcohol. Blot to remove the       stain.</li>\n  <li>Sponge with cold water.</li>\n  <li>If the stain remains, mix one teaspoon of liquid       dishwashing detergent and one tablespoon of ammonia with two cups of warm       water. Sponge the stain with this solution.  Leave it on the stain for at least 30       minutes, blotting every five minutes with a clean white cloth and more       solution.</li>\n  <li>Sponge  with cold water and blot dry.</li>\n</ol>\n				\n				<h3>Steps to Clean</h3>\n				<ol>\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n				</ol>','68.jpg');
INSERT INTO `stain` (`_id`, `stainof`, `fabric`, `need`, `steps`, `notes`, `img`)
VALUES
	(3,'Hair dye, black or brown','Upholstery','<li>Detergent</li>\n    <li>White vinegar</li>\n    <li>Rubbing alcohol</li>\n    <li>Detergent </li>\n    <li>Ammonia</li>','<li>Mix one (1) tablespoon of liquid dishwashing  detergent and one tablespoon of white vinegar with two (2) cups of cool water.</li>\n    <li>Using a clean white cloth, sponge the stain with the  detergent/vinegar solution. Leave it on the stain for at least 30 minutes,  blotting every five minutes with a clean white cloth and more solution. </li>\n    <li>Sponge with cool water, blot until the liquid is  absorbed.</li>\n    <li>Sponge the stain with rubbing alcohol; blot to remove  the stain.</li>\n    <li>If the stain remains, mix one teaspoon of liquid  dishwashing detergent and one tablespoon of ammonia with two cups of warm  water. (Caution: Never mix chlorine bleach and ammonia  – fumes are hazardous) </li>\n    <li>Sponge the stain with the detergent/ammonia  solution.  Leave it on the stain for at  least 30 minutes, blotting every five minutes with a clean white cloth and more  solution.</li>\n    <li>Sponge with cold water and blot dry.</li>','<h3>What you will need</h3>\n				<ul>\n    <li>Detergent</li>\n    <li>White vinegar</li>\n    <li>Rubbing alcohol</li>\n    <li>Detergent </li>\n    <li>Ammonia</li>\n  </ul>\n				<h3>Steps to Clean</h3>\n				<ol>\n    <li>Mix one (1) tablespoon of liquid dishwashing  detergent and one tablespoon of white vinegar with two (2) cups of cool water.</li>\n    <li>Using a clean white cloth, sponge the stain with the  detergent/vinegar solution. Leave it on the stain for at least 30 minutes,  blotting every five minutes with a clean white cloth and more solution. </li>\n    <li>Sponge with cool water, blot until the liquid is  absorbed.</li>\n    <li>Sponge the stain with rubbing alcohol; blot to remove  the stain.</li>\n    <li>If the stain remains, mix one teaspoon of liquid  dishwashing detergent and one tablespoon of ammonia with two cups of warm  water. (Caution: Never mix chlorine bleach and ammonia  – fumes are hazardous) </li>\n    <li>Sponge the stain with the detergent/ammonia  solution.  Leave it on the stain for at  least 30 minutes, blotting every five minutes with a clean white cloth and more  solution.</li>\n    <li>Sponge with cold water and blot dry.</li>\n  </ol>','68.jpg');
INSERT INTO `stain` (`_id`, `stainof`, `fabric`, `need`, `steps`, `notes`, `img`)
VALUES
	(4,'Contact glue','Washable Fabrics','<li>Pretreatment laundry stain remover </li>\n    <li>Heavy-duty  liquid detergent</li>\n    <li>Chlorine bleach or oxygen bleach</li>\n    <li>Dry cleaning solvent</li>\n    <li>Absorbent paper  towels</li>','<li>Saturate area  with pretreatment laundry stain remover (aerosol types work better on greasy  stains) Wait one minute for product to penetrate the stain. For stubborn stains, rub with heavy-duty  liquid detergent. Launder  immediately. </li>\n    <li>If color stain  remains, soak/wash in chlorine bleach if safe for fabric, or in oxygen bleach.</li>\n    <li>For extra heavy  stains, apply dry cleaning solvent to back of the stain over absorbent paper  towels. Let dry, rinse. Proceed as  above.</li>','<h3>What you will need</h3>\n					<ul><li>Pretreatment laundry stain remover </li>\n    <li>Heavy-duty  liquid detergent</li>\n    <li>Chlorine bleach or oxygen bleach</li>\n    <li>Dry cleaning solvent</li>\n    <li>Absorbent paper  towels</li>\n  </ul>\n				\n				\n				\n				<h3>Steps to Clean</h3>\n				<ol>\n    <li>Saturate area  with pretreatment laundry stain remover (aerosol types work better on greasy  stains) Wait one minute for product to penetrate the stain. For stubborn stains, rub with heavy-duty  liquid detergent. Launder  immediately. </li>\n    <li>If color stain  remains, soak/wash in chlorine bleach if safe for fabric, or in oxygen bleach.</li>\n    <li>For extra heavy  stains, apply dry cleaning solvent to back of the stain over absorbent paper  towels. Let dry, rinse. Proceed as  above.</li>\n  </ol>','119.jpg');
