INSERT INTO `stain` (`_id`, `stainof`, `fabric`, `need`, `steps`, `notes`, `img`)
VALUES
	(252,'Yellowing , dinginess or graying, general soil buildup, white or gray streaks','Washable Fabrics','<li>Detergent</li><li>Water conditioner</li>','<li>Fill the washer with hottest water appropriate for the fabric.</li>\n  <li>Add four times the normal amount of phosphate detergent and one cup of nonprecipitating water conditioner, such as Calgon or Spring Rain.</li>\n  <li>Agitate just long enough to  wet the clothes.</li>\n  <li>Soak overnight or about  12 hours.</li>\n  <li>Drain and spin without agitating.</li>\n  <li>Launder, using regular cycle,  no detergent, and one cup nonprecipitating water conditioner.</li>\n  <li>Repeat laundering  with no detergent and one cup of nonprecipitating water conditioner until no  suds appear during the rinses.</li>\n  <li>To remove  all dinginess, launder with nonprecipitating water conditioner and bleach that  is safe for the fabric. </li>','<h3>Steps to Clean</h3>\n				 <div class=\"boxChem\"><h3>Chemical solvents and supplies</h3><ul><li>Detergent</li><li>Water conditioner</li></ul></div><ol>\n  <li>Fill the washer with hottest water appropriate for the fabric.</li>\n  <li>Add four times the normal amount of phosphate detergent and one cup of nonprecipitating water conditioner, such as Calgon or Spring Rain.</li>\n  <li>Agitate just long enough to  wet the clothes.</li>\n  <li>Soak overnight or about  12 hours.</li>\n  <li>Drain and spin without agitating.</li>\n  <li>Launder, using regular cycle,  no detergent, and one cup nonprecipitating water conditioner.</li>\n  <li>Repeat laundering  with no detergent and one cup of nonprecipitating water conditioner until no  suds appear during the rinses.</li>\n  <li>To remove  all dinginess, launder with nonprecipitating water conditioner and bleach that  is safe for the fabric. </li>\n</ol>\n  <p>To  prevent hard water problems, use adequate amounts of low-sudsing phosphate  detergent or heave duty liquid detergent and water as hot as safe for  fabric.  Soften the water with  nonprecipitating water conditioner or install a water softening system.  Note: that water softening systems may increase  the amount of sodium in the drinking water and cause problems for people with  salt-restricted diets. \n  </p>','234.jpg');
INSERT INTO `stain` (`_id`, `stainof`, `fabric`, `need`, `steps`, `notes`, `img`)
VALUES
	(253,'Mustard','Washable Fabrics','<li>Heavy-duty liquid detergent</li>\n    <li>Dilute solution of all-fabric powdered bleach</li>\n    <li>Liquid chlorine bleach</li>\n    <li>Commercial color remover</li>','<li>Pretreat the  stain with heavy-duty liquid detergent. Rinse. </li>\n    <li>Soak fabric in  dilute solution of all-fabric powdered bleach. </li>\n    <li>If stain  persists and garment is white or colorfast, soak entire garment in diluted  solution of liquid chlorine bleach and water.  Chlorine bleach may change the color of the garment or cause  irreversible damage. Check for bleach  tolerance on a hidden seam. If stain  does not come out in 15 minutes of bleaching, if cannot be removed by  bleaching.</li>\n    <li>If bleaching is  not safe or does not work, use a commercial color remover according to package  directions. Note that color remover will  take out the fabric color as well as the stain.  Do not exceed 160 degrees Fahrenheit water with any synthetic fabrics*.  Launder.</li>','<h3>What you will need</h3>\n					<ul><li>Heavy-duty liquid detergent</li>\n    <li>Dilute solution of all-fabric powdered bleach</li>\n    <li>Liquid chlorine bleach</li>\n    <li>Commercial color remover</li>\n    </ul>\n				\n				\n				\n				<h3>Steps to Clean</h3>\n				<ol>\n    <li>Pretreat the  stain with heavy-duty liquid detergent. Rinse. </li>\n    <li>Soak fabric in  dilute solution of all-fabric powdered bleach. </li>\n    <li>If stain  persists and garment is white or colorfast, soak entire garment in diluted  solution of liquid chlorine bleach and water.  Chlorine bleach may change the color of the garment or cause  irreversible damage. Check for bleach  tolerance on a hidden seam. If stain  does not come out in 15 minutes of bleaching, if cannot be removed by  bleaching.</li>\n    <li>If bleaching is  not safe or does not work, use a commercial color remover according to package  directions. Note that color remover will  take out the fabric color as well as the stain.  Do not exceed 160 degrees Fahrenheit water with any synthetic fabrics*.  Launder.</li>\n  </ol>\n  <p>*<strong>Synthetic Fabrics</strong> are: Acetate, Acrylic, Polyester, Nylon, Olefin, Triacetate  and anything labeled static-resistant, wrinkle-resistant, permanent-press,  no-iron, stain-proof or moth-repellent</p>','72.jpg');
INSERT INTO `stain` (`_id`, `stainof`, `fabric`, `need`, `steps`, `notes`, `img`)
VALUES
	(254,'Mustard','Carpet','<li>Detergent</li>\n    <li>Ammonia</li>','<li>Scrape off excess foreign material.</li>\n					\n					\n					\n						<li>Mix one tablespoon of liquid dishwashing detergent with two cups of warm water.</li>\n					\n					\n					\n						<li>Using a clean white cloth, sponge the stain with the detergent solution.</li>\n					\n					\n					\n						<li>Blot until the liquid is absorbed.</li>\n					\n					\n					\n						<li>Repeat Steps 3 and 4 until the stain disappears.</li>\n					\n					\n					\n						<li>If the stain remains, mix one tablespoon of ammonia with two cups of warm water. (Caution: Never mix chlorine bleach and ammonia – fumes are hazardous) </li>\n					\n					\n					\n						<li>Sponge the stain with the ammonia solution.</li>\n					\n					\n					\n						<li>Blot until the liquid is absorbed.</li>\n					\n					\n					\n						<li>Sponge with cold water and blot dry. </li>','<h3>What you will need</h3>\n					<ul>\n    <li>Detergent</li>\n    <li>Ammonia</li>    \n</ul>\n				\n				\n				<h3>Steps to Clean</h3>\n				<ol>\n					\n					\n						<li>Scrape off excess foreign material.</li>\n					\n					\n					\n						<li>Mix one tablespoon of liquid dishwashing detergent with two cups of warm water.</li>\n					\n					\n					\n						<li>Using a clean white cloth, sponge the stain with the detergent solution.</li>\n					\n					\n					\n						<li>Blot until the liquid is absorbed.</li>\n					\n					\n					\n						<li>Repeat Steps 3 and 4 until the stain disappears.</li>\n					\n					\n					\n						<li>If the stain remains, mix one tablespoon of ammonia with two cups of warm water. (Caution: Never mix chlorine bleach and ammonia – fumes are hazardous) </li>\n					\n					\n					\n						<li>Sponge the stain with the ammonia solution.</li>\n					\n					\n					\n						<li>Blot until the liquid is absorbed.</li>\n					\n					\n					\n						<li>Sponge with cold water and blot dry. </li>\n					\n				</ol>\n				\n					<br/>\n					<h4>Or</h4>\n					<br/>\n				\n				\n			\n				\n				\n					<div class=\"boxChem\"><h3>Chemical solvents and supplies</h3><ul><li>Detergent</li><li>White vinegar</li><li>Enzymeproduct</li></ul></div><ol>\n  <li>Mix one (1) teaspoon liquid dishwashing       detergent with one (1) cup lukewarm water.  </li>\n  <li>Using a clean white cloth, sponge the stain with       the detergent solution.</li>\n  <li>Blot until the liquid is absorbed.</li>\n  <li>Repeat Steps 2 and 3 until the stain disappears.</li>\n  <li>If the stain remains, mix 1/3 cup white vinegar       with 2/3 cup of water.  </li>\n  <li>Apply using the clean white cloth, sponge the       stain with solution.  </li>\n  <li>If the stain still remains, mix a solution of       cool water and a product containing enzymes following direction on the       label or box.  (Some laundry       detergents contain enzymes.)</li>\n  <li>Cover the stain with a cloth drenched in the       solution.  Allow to remain on the       stain the length of time recommended by the manufacturer.  </li>\n  <li>Rinse the area with clean water.  Blot dry. </li>\n</ol>\n				\n				<h3>Steps to Clean</h3>\n				<ol>\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n					\n				</ol>','72.jpg');
