INSERT INTO `stain` (`_id`, `stainof`, `fabric`, `need`, `steps`, `notes`, `img`)
VALUES
	(343,'Red dye','Washable Fabrics','<li>Detergent</li><li>White vinegar</li><li>Color remover</li><li>Ammonia</li>','<li>Soak the item in a solution of 1 quart warm water, 1/2 teaspoon  liquid dishwashing or laundry detergent, and 1 tablespoon ammonia for 30  minutes.</li>\n  <li>Rinse well.</li>\n  <li>If stain persists, soak in a solution of 1 quart warm  water and 1 tablespoon white vinegar for 1 hour. Use white vinegar with care on cotton and linen.</li>\n  <li>Rinse well with water and allow to dry.</li>\n  <li>If stain is set, try  applying rubbing alcohol to the area and tamping (the method of bringing a brush down with light strokes on stained durable fabrics and materials).</li>\n  <li>As  stain loosens, blot liquid and stain with absorbent pad.</li>\n  <li>Keep both the stain and pad moist with alcohol and change pad as it picks up stain.</li>\n  <li>Allow to dry.</li>\n  <li>As a last resort for any remaining traces of stain, mix Rit Color Remover according to package direction and apply to stain.</li>\n  <li>After testing on a hidden  place, flush the solution through the stain.</li>\n  <li>Rinse well with clear water and  allow to dry thoroughly. </li>','<h3>Steps to Clean</h3>\n				 <div class=\"boxChem\"><h3>Chemical solvents and supplies</h3><ul><li>Detergent</li><li>White vinegar</li><li>Color remover</li><li>Ammonia</li></ul></div><ol>\n  <li>Soak the item in a solution of 1 quart warm water, 1/2 teaspoon  liquid dishwashing or laundry detergent, and 1 tablespoon ammonia for 30  minutes.</li>\n  <li>Rinse well.</li>\n  <li>If stain persists, soak in a solution of 1 quart warm  water and 1 tablespoon white vinegar for 1 hour. Use white vinegar with care on cotton and linen.</li>\n  <li>Rinse well with water and allow to dry.</li>\n  <li>If stain is set, try  applying rubbing alcohol to the area and tamping (the method of bringing a brush down with light strokes on stained durable fabrics and materials).</li>\n  <li>As  stain loosens, blot liquid and stain with absorbent pad.</li>\n  <li>Keep both the stain and pad moist with alcohol and change pad as it picks up stain.</li>\n  <li>Allow to dry.</li>\n  <li>As a last resort for any remaining traces of stain, mix Rit Color Remover according to package direction and apply to stain.</li>\n  <li>After testing on a hidden  place, flush the solution through the stain.</li>\n  <li>Rinse well with clear water and  allow to dry thoroughly. </li>\n</ol>','224.jpg');
